  `country_id` int(11) NOT NULL,
  `theme_id` int(11) NOT NULL,
  `type_id` int(11) NOT NULL,
  `space` varchar(128) NOT NULL DEFAULT '',
  `book_count` int(11) NOT NULL DEFAULT '0',
  `info` varchar(512) NOT NULL DEFAULT '',
  `create_date` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `author` varchar(64) NOT NULL DEFAULT '鲁迅',
  PRIMARY KEY (`id`),
  KEY `country_id` (`country_id`),
  KEY `theme_id` (`theme_id`),
  KEY `type_id` (`type_id`),
  CONSTRAINT `lib_book_ibfk_1` FOREIGN KEY (`country_id`) REFERENCES `lib_country` (`id`),
  CONSTRAINT `lib_book_ibfk_2` FOREIGN KEY (`theme_id`) REFERENCES `lib_theme` (`id`),
  CONSTRAINT `lib_book_ibfk_3` FOREIGN KEY (`type_id`) REFERENCES `lib_type` (`id`)
