  `examine_state` tinyint(1) NOT NULL DEFAULT '0',
  `borrow_state` tinyint(1) NOT NULL DEFAULT '0',
  `renew_state` tinyint(1) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`),
  KEY `user_id` (`user_id`),
  KEY `book_id` (`book_id`),
  CONSTRAINT `lib_borrow_info_ibfk_1` FOREIGN KEY (`user_id`) REFERENCES `lib_user` (`id`) ON DELETE CASCADE,
  CONSTRAINT `lib_borrow_info_ibfk_2` FOREIGN KEY (`book_id`) REFERENCES `lib_book` (`id`) ON DELETE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=109 DEFAULT CHARSET=utf8mb4;
