mapper.select:
select b.id,b.book_name, c.id cid,c.country_name, t.id tid,t.theme_name, p.id pid,p.type_name, b.space, b.book_count, b.info, b.create_date from lib_book b LEFT JOIN lib_country c on b.country_id=c.id LEFT JOIN lib_theme t on b.theme_id=t.id LEFT JOIN lib_type p on b.type_id=p.id
b.book_name like "%"#{name}"%"
and b.country_id=#{country_id}
and b.theme_id=#{theme_id}
and space=#{space}
and b.type_id=#{type_id}
order by b.create_date desc

select b.id,b.book_name, c.id cid,c.country_name, t.id tid,t.theme_name, p.id pid,p.type_name, b.space, b.book_count, b.info, b.create_date from lib_book b LEFT JOIN lib_country c on b.country_id=c.id LEFT JOIN lib_theme t on b.theme_id=t.id LEFT JOIN lib_type p on b.type_id=p.id
b.book_name like "%"#{name}"%"
and b.country_id=#{country_id}
and b.theme_id=#{theme_id}
and space=#{space}
and b.type_id=#{type_id}
order by b.create_date desc limit #{current},#{size}
